import re

with open('/testbed/input/The-State-of-Competition.txt', 'r') as f:
    content = f.read()

# Split into paragraphs (assuming double newline)
paragraphs = content.split('\n\n')

industries = []
for p in paragraphs:
    # Look for patterns like "X infringement" or "X infringements"
    # and industry mention
    if 'infringement' in p.lower() or 'contravention' in p.lower():
        # Look for numbers
        numbers = re.findall(r'\b\d+\b', p)
        # Look for industry names - heuristic
        # Common pattern: "in the X industry"
        matches = re.findall(r'in the ([A-Za-z]+ industry)', p)
        if matches:
            for match in matches:
                # Check if there is a number in the same sentence
                sentences = re.split(r'[.!?]', p)
                for sent in sentences:
                    if match in sent:
                        nums = re.findall(r'\b\d+\b', sent)
                        if nums:
                            industries.append((match, nums))
                        break
        # also look for "X industry" without "in the"
        matches2 = re.findall(r'([A-Za-z]+ industry)', p)
        for match in matches2:
            if match not in [i[0] for i in industries]:
                # check if number in same sentence
                sentences = re.split(r'[.!?]', p)
                for sent in sentences:
                    if match in sent:
                        nums = re.findall(r'\b\d+\b', sent)
                        if nums:
                            industries.append((match, nums))
                        break

print("Found industries with numbers:", industries)

# Also search for lines with numbers and "infringement"
lines = content.split('\n')
for i, line in enumerate(lines):
    if 'infringement' in line.lower():
        print(f"Line {i+1}: {line.strip()}")
